import React, { useState, useEffect } from 'react';
// Importaciones de Firebase (una sola vez y agrupadas)
import { initializeApp } from 'firebase/app';
import { 
    getAuth, 
    onAuthStateChanged, 
    createUserWithEmailAndPassword, 
    signInWithEmailAndPassword, 
    signOut 
} from 'firebase/auth';
import { 
    getFirestore, 
    collection, 
    addDoc, 
    query, 
    where, 
    getDocs, 
    doc, 
    getDoc,
    updateDoc,
    onSnapshot,
    setDoc
} from 'firebase/firestore';
import { 
    getStorage, 
    ref, 
    uploadBytesResumable, 
    getDownloadURL 
} from 'firebase/storage';

// --- INSTRUCCIONES DE CONFIGURACIÓN DE FIREBASE ---
// 1. Ve a https://firebase.google.com/ y crea un nuevo proyecto.
// 2. En tu proyecto de Firebase, ve a "Project Settings" (Configuración del proyecto).
// 3. En la pestaña "General", desplázate hacia abajo hasta "Your apps" (Tus aplicaciones).
// 4. Haz clic en el ícono web (</>) para registrar una nueva aplicación web.
// 5. Nombra tu aplicación y haz clic en "Register app".
// 6. Firebase te proporcionará un objeto de configuración. Cópialo y pégalo aquí.
const firebaseConfig = {
  apiKey: "AIzaSyA7H6G0mXCy9DmoLg3fSW3TrxIRcEzz9jg",
  authDomain: "portal-evelsa.firebaseapp.com",
  projectId: "portal-evelsa",
  storageBucket: "portal-evelsa.firebasestorage.app",
  messagingSenderId: "847174487471",
  appId: "1:847174487471:web:c3a57fd8315ce619a2335a"
};

// --- Inicialización de Firebase ---
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);
const storage = getStorage(app);

// --- Componentes de la Interfaz ---

const Header = ({ user, userRole }) => (
    <header className="bg-white shadow-md">
        <div className="max-w-7xl mx-auto py-4 px-4 sm:px-6 lg:px-8 flex justify-between items-center">
            <div className="flex items-center">
                 {/* Podrías usar el logo de tu empresa aquí */}
                <svg className="w-10 h-10 text-blue-800 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19.428 15.428a2 2 0 00-1.022-.547l-2.387-.477a6 6 0 00-3.86.517l-.318.158a6 6 0 01-3.86.517L6.05 15.21a2 2 0 00-1.806.547M8 4h8l-1 1v5.172a2 2 0 00.586 1.414l5 5c1.26 1.26.367 3.414-1.415 3.414H4.828c-1.782 0-2.674-2.154-1.414-3.414l5-5A2 2 0 009 10.172V5L8 4z"></path></svg>
                <h1 className="text-2xl font-bold text-gray-800">Portal Grupo Evelsa</h1>
            </div>
            {user && (
                <div className="flex items-center">
                    <span className="text-gray-600 mr-4">Hola, {user.email} ({userRole})</span>
                    <button
                        onClick={() => signOut(auth)}
                        className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded-lg transition duration-300"
                    >
                        Cerrar Sesión
                    </button>
                </div>
            )}
        </div>
    </header>
);

const ProjectCard = ({ project, userRole, onUpdateProject }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [heyazineUrl, setHeyazineUrl] = useState(project.urlHeyzine || '');
    const [file, setFile] = useState(null);
    const [uploadProgress, setUploadProgress] = useState(0);

    const handleFileChange = (e) => {
        if (e.target.files[0]) {
            setFile(e.target.files[0]);
        }
    };

    const handleUpdate = async () => {
        let pdfUrl = project.urlPdf;
        if (file) {
            const storageRef = ref(storage, `proyectos/${project.id}/${file.name}`);
            const uploadTask = uploadBytesResumable(storageRef, file);

            uploadTask.on('state_changed',
                (snapshot) => {
                    const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                    setUploadProgress(progress);
                },
                (error) => {
                    console.error("Error al subir archivo:", error);
                    alert("Error al subir el archivo.");
                },
                async () => {
                    const downloadURL = await getDownloadURL(uploadTask.snapshot.ref);
                    pdfUrl = downloadURL;
                    await finishUpdate(pdfUrl);
                }
            );
        } else {
            await finishUpdate(pdfUrl);
        }
    };
    
    const finishUpdate = async (finalPdfUrl) => {
        const projectRef = doc(db, "proyectos", project.id);
        await updateDoc(projectRef, {
            urlHeyzine: heyazineUrl,
            urlPdf: finalPdfUrl,
            estado: 'Terminado'
        });
        setIsEditing(false);
        onUpdateProject(); // Para refrescar la lista
    };


    return (
        <div className="bg-white rounded-xl shadow-lg overflow-hidden p-6 border border-gray-200">
            <h3 className="text-xl font-bold text-blue-800">{project.nombreProyecto}</h3>
            <p className="text-gray-600 mt-1">Cliente: {project.clienteEmail}</p>
            <p className={`mt-2 font-semibold ${project.estado === 'Activo' ? 'text-orange-500' : 'text-green-600'}`}>
                Estado: {project.estado}
            </p>

            {userRole === 'cliente' && project.estado === 'Terminado' && (
                <div className="mt-4 flex flex-col sm:flex-row space-y-2 sm:space-y-0 sm:space-x-3">
                    <a href={project.urlHeyzine} target="_blank" rel="noopener noreferrer" className="flex-1 text-center bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg transition duration-300">
                        Ver Flipbook
                    </a>
                    <a href={project.urlPdf} target="_blank" rel="noopener noreferrer" download className="flex-1 text-center bg-gray-600 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded-lg transition duration-300">
                        Descargar PDF
                    </a>
                </div>
            )}
            
            {userRole === 'empleado' && !isEditing && project.estado === 'Activo' && (
                 <button onClick={() => setIsEditing(true)} className="mt-4 w-full bg-yellow-500 hover:bg-yellow-600 text-white font-bold py-2 px-4 rounded-lg transition duration-300">
                    Finalizar Proyecto
                </button>
            )}

            {userRole === 'empleado' && isEditing && (
                <div className="mt-4 space-y-4">
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Enlace de Heyzine</label>
                        <input
                            type="text"
                            value={heyazineUrl}
                            onChange={(e) => setHeyazineUrl(e.target.value)}
                            placeholder="https://heyzine.com/..."
                            className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                        />
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Subir PDF Final</label>
                        <input
                            type="file"
                            accept=".pdf"
                            onChange={handleFileChange}
                            className="mt-1 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                        />
                    </div>
                     {uploadProgress > 0 && <div className="w-full bg-gray-200 rounded-full h-2.5"><div className="bg-blue-600 h-2.5 rounded-full" style={{width: `${uploadProgress}%`}}></div></div>}
                    <div className="flex space-x-3">
                        <button onClick={handleUpdate} className="flex-1 bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded-lg transition duration-300">
                            Guardar y Finalizar
                        </button>
                         <button onClick={() => setIsEditing(false)} className="flex-1 bg-gray-400 hover:bg-gray-500 text-white font-bold py-2 px-4 rounded-lg transition duration-300">
                            Cancelar
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};


const NewProjectForm = ({ onProjectAdded }) => {
    const [nombreProyecto, setNombreProyecto] = useState('');
    const [clienteEmail, setClienteEmail] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        if (!nombreProyecto || !clienteEmail) {
            setError('Todos los campos son obligatorios.');
            return;
        }

        try {
            // Verificamos si existe un usuario con ese email y rol de cliente
            const q = query(collection(db, "usuarios"), where("email", "==", clienteEmail), where("rol", "==", "cliente"));
            const querySnapshot = await getDocs(q);

            if (querySnapshot.empty) {
                setError('No se encontró un cliente con ese correo electrónico. Asegúrese de que el cliente esté registrado.');
                return;
            }
            
            const clientUser = querySnapshot.docs[0];

            await addDoc(collection(db, "proyectos"), {
                nombreProyecto,
                clienteEmail,
                clienteId: clientUser.id, // Guardamos el ID del usuario cliente
                estado: 'Activo',
                urlHeyzine: '',
                urlPdf: ''
            });

            setNombreProyecto('');
            setClienteEmail('');
            onProjectAdded();
        } catch (err) {
            console.error("Error al crear proyecto:", err);
            setError('Ocurrió un error al crear el proyecto.');
        }
    };

    return (
        <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200 mb-8">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">Crear Nuevo Proyecto</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-sm font-medium text-gray-700">Nombre del Proyecto</label>
                    <input type="text" value={nombreProyecto} onChange={(e) => setNombreProyecto(e.target.value)} className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"/>
                </div>
                <div>
                    <label className="block text-sm font-medium text-gray-700">Email del Cliente</label>
                    <input type="email" value={clienteEmail} onChange={(e) => setClienteEmail(e.target.value)} className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"/>
                </div>
                {error && <p className="text-red-500 text-sm">{error}</p>}
                <button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg transition duration-300">
                    Añadir Proyecto
                </button>
            </form>
        </div>
    );
};

const Dashboard = ({ user, userRole }) => {
    const [projects, setProjects] = useState([]);
    const [loading, setLoading] = useState(true);

    const fetchProjects = () => {
        setLoading(true);
        let q;
        if (userRole === 'empleado') {
            q = query(collection(db, "proyectos"));
        } else {
            q = query(collection(db, "proyectos"), where("clienteId", "==", user.uid));
        }

        // Usamos onSnapshot para escuchar cambios en tiempo real
        const unsubscribe = onSnapshot(q, (querySnapshot) => {
            const projectsData = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
             // Ordenar en memoria para evitar índices de Firestore
            projectsData.sort((a, b) => (a.estado > b.estado) ? 1 : -1);
            setProjects(projectsData);
            setLoading(false);
        }, (error) => {
            console.error("Error fetching projects: ", error);
            setLoading(false);
        });
        
        return () => unsubscribe(); // Limpiamos el listener al desmontar
    };

    useEffect(() => {
        const unsubscribe = fetchProjects();
        return unsubscribe;
    }, [user, userRole]);


    return (
        <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
            <h1 className="text-3xl font-bold text-gray-900 mb-6">
                {userRole === 'empleado' ? 'Panel de Empleado' : 'Mis Proyectos'}
            </h1>

            {userRole === 'empleado' && <NewProjectForm onProjectAdded={fetchProjects} />}
            
            {loading ? (
                <p>Cargando proyectos...</p>
            ) : projects.length === 0 ? (
                <p className="text-center text-gray-500 mt-8">No hay proyectos para mostrar.</p>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {projects.map(project => (
                        <ProjectCard key={project.id} project={project} userRole={userRole} onUpdateProject={fetchProjects} />
                    ))}
                </div>
            )}
        </div>
    );
};


const AuthPage = () => {
    const [isLogin, setIsLogin] = useState(true);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('cliente'); // 'cliente' o 'empleado'
    const [error, setError] = useState('');

    const handleAuth = async (e) => {
        e.preventDefault();
        setError('');
        try {
            if (isLogin) {
                await signInWithEmailAndPassword(auth, email, password);
                // El onAuthStateChanged se encargará de la redirección
            } else {
                // Registro
                if (role === 'empleado') {
                    setError('El registro de empleados está deshabilitado desde esta interfaz.');
                    return;
                }
                const userCredential = await createUserWithEmailAndPassword(auth, email, password);
                // Guardar el rol del usuario en Firestore
                await setDoc(doc(db, "usuarios", userCredential.user.uid), {
                    email: userCredential.user.email,
                    rol: role
                });
            }
        } catch (err) {
            setError(err.message);
            console.error("Error de autenticación:", err.code, err.message);
        }
    };
    
    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100">
            <div className="max-w-md w-full bg-white p-8 rounded-xl shadow-lg">
                <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">{isLogin ? 'Iniciar Sesión' : 'Registrar Cliente'}</h2>
                <form onSubmit={handleAuth} className="space-y-6">
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Email</label>
                        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"/>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Contraseña</label>
                        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"/>
                    </div>
                    
                    {!isLogin && (
                        <p className="text-sm text-gray-600">Creando una cuenta de **Cliente**. Los empleados deben ser registrados por un administrador.</p>
                    )}
                    
                    {error && <p className="text-red-500 text-sm text-center">{error}</p>}
                    
                    <button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg transition duration-300">
                        {isLogin ? 'Entrar' : 'Registrarse'}
                    </button>
                </form>
                <p className="text-center text-sm text-gray-600 mt-6">
                    {isLogin ? '¿No tienes cuenta de cliente?' : '¿Ya tienes una cuenta?'}
                    <button onClick={() => setIsLogin(!isLogin)} className="font-medium text-blue-600 hover:text-blue-500 ml-1">
                        {isLogin ? 'Regístrate aquí' : 'Inicia sesión'}
                    </button>
                </p>
            </div>
        </div>
    );
};


export default function App() {
    const [user, setUser] = useState(null);
    const [userRole, setUserRole] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
            if (currentUser) {
                // Usuario ha iniciado sesión, obtener su rol de Firestore
                const userDocRef = doc(db, "usuarios", currentUser.uid);
                const userDoc = await getDoc(userDocRef);
                if (userDoc.exists()) {
                    setUserRole(userDoc.data().rol);
                } else {
                     // Caso raro: usuario autenticado pero sin documento en Firestore.
                     // Por seguridad, lo deslogueamos.
                    signOut(auth);
                }
                setUser(currentUser);
            } else {
                setUser(null);
                setUserRole(null);
            }
            setLoading(false);
        });

        // Limpieza al desmontar el componente
        return () => unsubscribe();
    }, []);

    if (loading) {
        return <div className="min-h-screen flex items-center justify-center">Cargando...</div>;
    }

    return (
        <div className="bg-gray-50 min-h-screen font-sans">
             <Header user={user} userRole={userRole}/>
             <main>
                {user && userRole ? <Dashboard user={user} userRole={userRole} /> : <AuthPage />}
             </main>
        </div>
    );
}
