const Dashboard = ({ user, userData }) => {
    
    const renderDashboardByRole = () => {
        switch (userData.rol) {
            case 'administrador':
                return <AdminDashboard />;
            case 'cliente':
                return <ClientDashboard user={user} userData={userData} />;
            case 'directivo':
