const PDFFlipbookViewer = ({ pdfUrl, onClose }) => {
    const flipbookRef = React.useRef(null);
    const [statusMessage, setStatusMessage] = useState("Cargando documento...");
    const [isError, setIsError] = useState(false);

    useEffect(() => {
        let isMounted = true;
        let checkInterval;

        const checkLibrariesAndInitialize = (attempts = 0) => {
            // Si las librerías están listas, empieza a construir el visor
            if (window.jQuery && window.jQuery.fn.turn) {
                if (isMounted) {
                    clearInterval(checkInterval);
                    initializeViewer();
                }
                return;
            }

            // Si no, y hemos intentado menos de 20 veces (10 segundos), sigue esperando
            if (attempts < 20) {
                 // No hacer nada, el intervalo seguirá intentando
            } else {
                // Si después de 10 segundos no cargan, muestra el error
                if (isMounted) {
                    clearInterval(checkInterval);
                    setStatusMessage("Error: Un componente del visor no se cargó a tiempo. Revise la conexión a internet.");
                    setIsError(true);
                }
            }
        };

        const initializeViewer = async () => {
            // Cargar la librería de pdf.js
            const pdfJsSrc = "https://cdnjs.cloudflare.com/ajax/libs/pdf.js/2.11.338/pdf.min.js";
            const script = document.createElement('script');
            script.src = pdfJsSrc;
            script.id = pdfJsSrc;
            
            script.onload = async () => {
                try {
                    const pdfjsLib = window['pdfjs-dist/build/pdf'];
                    pdfjsLib.GlobalWorkerOptions.workerSrc = `https://cdnjs.cloudflare.com/ajax/libs/pdf.js/2.11.338/pdf.worker.min.js`;

                    const pdf = await pdfjsLib.getDocument(pdfUrl).promise;
                    const flipbookEl = flipbookRef.current;
                    if (!flipbookEl || !isMounted) return;

                    flipbookEl.innerHTML = '';
                    setStatusMessage(`Procesando ${pdf.numPages} páginas...`);

                    const pagePromises = [];
                    for (let num = 1; num <= pdf.numPages; num++) {
                        pagePromises.push(
                            pdf.getPage(num).then(page => {
                                const viewport = page.getViewport({ scale: 1.5 });
                                const canvas = document.createElement('canvas');
                                const context = canvas.getContext('2d');
                                canvas.height = viewport.height;
                                canvas.width = viewport.width;
                                const pageDiv = document.createElement('div');
                                pageDiv.appendChild(canvas);
                                return page.render({ canvasContext: context, viewport: viewport }).promise.then(() => pageDiv);
                            })
                        );
                    }
                    
                    const pages = await Promise.all(pagePromises);
                    pages.forEach(pageEl => flipbookEl.appendChild(pageEl));

                    if (!isMounted) return;
                    setStatusMessage("Creando libro interactivo...");
                    
                    window.$(flipbookEl).turn({
                        width: flipbookEl.children[0].children[0].width,
                        height: flipbookEl.children[0].children[0].height,
                        autoCenter: true,
                        elevation: 50,
                        duration: 800,
                    });
                    
                    if (isMounted) setStatusMessage("");

                } catch (error) {
                    console.error("Error detallado en el visor:", error);
                    if (isMounted) {
                        setStatusMessage("Error: No se pudo cargar el libro interactivo.");
                        setIsError(true);
                    }
                }
            };
            document.body.appendChild(script);
        };

        // Inicia el chequeo repetitivo
        checkInterval = setInterval(checkLibrariesAndInitialize, 500);

        return () => {
            isMounted = false;
            clearInterval(checkInterval);
            if (window.$ && flipbookRef.current && window.$(flipbookRef.current).turn('instance')) {
                window.$(flipbookRef.current).turn('destroy');
            }
            const scriptEl = document.getElementById("https://cdnjs.cloudflare.com/ajax/libs/pdf.js/2.11.338/pdf.min.js");
            if (scriptEl) document.body.removeChild(scriptEl);
        };

    }, [pdfUrl]);


    return (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center z-50">
            <div className="bg-white rounded-lg shadow-xl w-full h-full max-w-6xl max-h-[90vh] flex flex-col p-4">
                <button onClick={onClose} className="self-end text-gray-600 hover:text-gray-900 text-3xl font-light leading-none z-10">&times;</button>
                <div className="flex-grow flex items-center justify-center relative -mt-10">
                    {statusMessage && (
                        <div className={`text-center font-semibold ${isError ? 'text-red-600' : 'text-gray-700'}`}>
                            {statusMessage}
                        </div>
                    )}
                    <div ref={flipbookRef} style={{ display: statusMessage ? 'none' : 'block', width: '100%', height: '100%' }}>
                    </div>
                </div>
            </div>
        </div>
    );
};

