const Dashboard = ({ user, userData }) => {
    const [projects, setProjects] = useState([]);
    const [loading, setLoading] = useState(true);
    const [view, setView] = useState('projects'); 

    useEffect(() => {
        if (!user || !userData) { setLoading(false); return; }
        let q;
        if (userData.rol === 'empleado') {
            q = query(collection(db, "proyectos"), orderBy("fechaApertura", "desc"));
        } else {
            q = query(collection(db, "proyectos"), where("clienteId", "==", user.uid), where("estado", "in", ["Activo", "Terminado"]), orderBy("fechaApertura", "desc"));
        }
        const unsubscribe = onSnapshot(q, (querySnapshot) => {
            const projectsData = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
            setProjects(projectsData);
            setLoading(false);
        }, (error) => {
            console.error("Error fetching projects: ", error);
            if (error.code === 'failed-precondition') console.error("IMPORTANTE: Se requiere un índice compuesto en Firestore. Ve a la URL en la consola para crearlo.");
            setLoading(false);
        });
        return () => unsubscribe();
    }, [user, userData]);

    const EmployeeView = () => (
         <>
            <div className="mb-6 border-b border-gray-200"><nav className="-mb-px flex space-x-8"><button onClick={() => setView('projects')} className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${view === 'projects' ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500'}`}>Proyectos</button><button onClick={() => setView('users')} className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${view === 'users' ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500'}`}>Usuarios</button></nav></div>
