import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { 
    getAuth, 
    onAuthStateChanged, 
    createUserWithEmailAndPassword, 
    signInWithEmailAndPassword, 
    signOut 
} from 'firebase/auth';
import { 
    getFirestore, 
    collection, 
    addDoc, 
    query, 
    where, 
    getDocs, 
    doc, 
    getDoc,
    updateDoc,
    onSnapshot,
    setDoc,
    Timestamp,
    runTransaction,
    orderBy,
    deleteDoc
} from 'firebase/firestore';
import { 
    getStorage, 
    ref, 
    uploadBytesResumable, 
    getDownloadURL 
} from 'firebase/storage';

// --- INSTRUCCIONES DE CONFIGURACIÓN DE FIREBASE ---
const firebaseConfig = {
  apiKey: "AIzaSyA7H6G0mXCy9DmoLg3fSW3TrxIRcEzz9jg",
  authDomain: "portal-evelsa.firebaseapp.com",
  projectId: "portal-evelsa",
  storageBucket: "portal-evelsa.firebasestorage.app",
  messagingSenderId: "847174487471",
  appId: "1:847174487471:web:c3a57fd8315ce619a2335a"
};

// --- Inicialización de Firebase ---
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);
const storage = getStorage(app);

// --- Componente de Alerta Reutilizable ---
const Alert = ({ message, type, onClose }) => {
    if (!message) return null;
    const baseClasses = "p-4 mb-4 text-sm rounded-lg relative";
    const typeClasses = {
        error: "bg-red-100 text-red-700",
        success: "bg-green-100 text-green-700"
    };
    return (
        <div className={`${baseClasses} ${typeClasses[type]}`} role="alert">
            <span className="font-medium">{message}</span>
            <button onClick={onClose} className="absolute top-0 right-0 mt-2 mr-3 text-2xl font-semibold leading-none">&times;</button>
        </div>
    );
};

// --- Componente de Cabecera ---
const Header = ({ user, userData }) => (
    <header className="bg-white shadow-md sticky top-0 z-40">
        <div className="max-w-7xl mx-auto py-4 px-4 sm:px-6 lg:px-8 flex justify-between items-center">
            <div className="flex items-center">
                 {userData && userData.rol === 'cliente' && userData.logoUrl ? (
                    <img src={userData.logoUrl} onError={(e)=>{e.target.onerror = null; e.target.src="https://placehold.co/100x40/003366/FFFFFF?text=Logo"}} alt="Logo Cliente" className="h-10 w-auto mr-3 object-contain"/>
                ) : (
                    <svg className="w-10 h-10 text-blue-800 mr-3" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19.428 15.428a2 2 0 00-1.022-.547l-2.387-.477a6 6 0 00-3.86.517l-.318.158a6 6 0 01-3.86.517L6.05 15.21a2 2 0 00-1.806.547M8 4h8l-1 1v5.172a2 2 0 00.586 1.414l5 5c1.26 1.26.367 3.414-1.415 3.414H4.828c-1.782 0-2.674-2.154-1.414-3.414l5-5A2 2 0 009 10.172V5L8 4z"></path></svg>
                )}
                <h1 className="text-2xl font-bold text-gray-800">Portal Grupo Evelsa</h1>
            </div>
            {user && (
                <div className="flex items-center">
                    <span className="text-gray-600 mr-4">
                        Hola, {userData ? userData.nombreCompleto : user.email}
                    </span>
                    <button
                        onClick={() => signOut(auth)}
                        className="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded-lg transition duration-300"
                    >
                        Cerrar Sesión
                    </button>
                </div>
            )}
        </div>
    </header>
);

// --- Componentes de Gestión (Para Empleados) ---

const UserManagement = ({ onUserAdded }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [nombreCompleto, setNombreCompleto] = useState('');
    const [rol, setRol] = useState('cliente');
    const [clienteIdNumerico, setClienteIdNumerico] = useState('');
    const [logoUrl, setLogoUrl] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const resetForm = () => {
        setEmail(''); setPassword(''); setNombreCompleto(''); setRol('cliente');
        setClienteIdNumerico(''); setLogoUrl('');
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(''); setSuccess('');

        if (!nombreCompleto || !email || !password || (rol === 'cliente' && !clienteIdNumerico)) {
            setError('Por favor, complete todos los campos requeridos.');
            return;
        }

        try {
            const tempApp = initializeApp(firebaseConfig, 'Secondary');
            const tempAuth = getAuth(tempApp);
            const userCredential = await createUserWithEmailAndPassword(tempAuth, email, password);
            
            await setDoc(doc(db, "usuarios", userCredential.user.uid), {
                email, rol, nombreCompleto,
                clienteIdNumerico: rol === 'cliente' ? clienteIdNumerico.padStart(3, '0') : '',
                logoUrl: rol === 'cliente' ? logoUrl : '',
            });

            setSuccess(`¡Usuario ${email} creado con éxito!`);
            resetForm();
            if (onUserAdded) onUserAdded();
        } catch (err) {
            setError(`Error al crear usuario: ${err.message}`);
        }
    };

    return (
        <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200">
            <h3 className="text-xl font-bold text-gray-800 mb-4">Crear Usuario (Cliente o Empleado)</h3>
            <form onSubmit={handleSubmit} className="space-y-4">
                 <input type="text" value={nombreCompleto} onChange={e => setNombreCompleto(e.target.value)} placeholder="Nombre Completo" className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                 <select value={rol} onChange={e => setRol(e.target.value)} className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm">
                    <option value="cliente">Cliente</option>
                    <option value="empleado">Empleado</option>
                 </select>
                 <input type="email" value={email} onChange={e => setEmail(e.target.value)} placeholder="Email de Acceso" className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                 <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Contraseña (mínimo 6 caracteres)" className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                 {rol === 'cliente' && (
                     <>
                        <input type="text" value={clienteIdNumerico} onChange={e => setClienteIdNumerico(e.target.value)} placeholder="ID Numérico Cliente (ej: 001)" className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                        <input type="text" value={logoUrl} onChange={e => setLogoUrl(e.target.value)} placeholder="URL del Logo (Opcional)" className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                     </>
                 )}
                 <Alert message={error} type="error" onClose={() => setError('')} />
                 <Alert message={success} type="success" onClose={() => setSuccess('')} />
                 <button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg">Crear Usuario</button>
            </form>
        </div>
    );
};


const NewProjectForm = ({ onProjectAdded }) => {
    // MODIFICACIÓN 1: Añadir fechaApertura al estado inicial del formulario
    const [formData, setFormData] = useState({ 
        clienteId: '', 
        servicioId: '', 
        proveedorId: '', 
        comentarios: '',
        fechaApertura: new Date().toISOString().split('T')[0] // Default to today
    });
    const [collections, setCollections] = useState({ clientes: [], servicios: [], proveedores: [] });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [clientesSnap, serviciosSnap, proveedoresSnap] = await Promise.all([
                    getDocs(query(collection(db, "usuarios"), where("rol", "==", "cliente"))),
                    getDocs(collection(db, "servicios")),
                    getDocs(collection(db, "proveedores"))
                ]);
                setCollections({
                    clientes: clientesSnap.docs.map(doc => ({ id: doc.id, ...doc.data() })),
                    servicios: serviciosSnap.docs.map(doc => ({ id: doc.id, ...doc.data() })),
                    proveedores: proveedoresSnap.docs.map(doc => ({ id: doc.id, ...doc.data() }))
                });
            } catch (e) {
                setError("Error al cargar datos. Asegúrese de que las colecciones 'servicios' y 'proveedores' existan.");
            }
        };
        fetchData();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!formData.clienteId || !formData.servicioId || !formData.proveedorId || !formData.fechaApertura) {
            setError('Todos los campos son obligatorios.');
            return;
        }
        setLoading(true);
        setError('');

        try {
            const anioActual = new Date(formData.fechaApertura).getFullYear(); // Usar año de la fecha de apertura
            const contadorRef = doc(db, "contadores", `proyectos_${anioActual}`);
            
            const nuevoConsecutivo = await runTransaction(db, async (transaction) => {
                const contadorDoc = await transaction.get(contadorRef);
                if (!contadorDoc.exists()) {
                    transaction.set(contadorRef, { consecutivo: 1 });
                    return 1;
                }
                const nuevoValor = contadorDoc.data().consecutivo + 1;
                transaction.update(contadorRef, { consecutivo: nuevoValor });
                return nuevoValor;
            });
            
            const ultimosDosDigitosAnio = anioActual.toString().slice(-2);
            const consecutivoFormateado = nuevoConsecutivo.toString().padStart(3, '0');
            
            const cliente = collections.clientes.find(c => c.id === formData.clienteId);
            const servicio = collections.servicios.find(s => s.id === formData.servicioId);
            const proveedor = collections.proveedores.find(p => p.id === formData.proveedorId);

            const npu = `${cliente.clienteIdNumerico}-${servicio.servicioIdNumerico}-${proveedor.proveedorIdNumerico}-${consecutivoFormateado}${ultimosDosDigitosAnio}`;

            await addDoc(collection(db, "proyectos"), {
                // MODIFICACIÓN 1: Usar la fecha del formulario
                fechaApertura: Timestamp.fromDate(new Date(formData.fechaApertura)),
                npu,
                clienteId: cliente.id,
                clienteNombre: cliente.nombreCompleto,
                servicioId: servicio.id,
                servicioNombre: servicio.nombre,
                proveedorId: proveedor.id,
                proveedorNombre: proveedor.nombre,
                comentarios: formData.comentarios,
                // MODIFICACIÓN 2: Estado inicial es "Cotización"
                estado: 'Cotización',
                fechaEntrega: null,
                notaEntrega: '',
                urlHeyzine: '',
                urlPdf: ''
            });
            
            setFormData({ clienteId: '', servicioId: '', proveedorId: '', comentarios: '', fechaApertura: new Date().toISOString().split('T')[0] });
            if (onProjectAdded) onProjectAdded();

        } catch (err) {
            console.error("Error al crear proyecto:", err);
            setError(`Ocurrió un error: ${err.message}`);
        }
        setLoading(false);
    };

    return (
        <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200 mb-8">
            <h3 className="text-xl font-bold text-gray-800 mb-4">Crear Nuevo Proyecto</h3>
            <form onSubmit={handleSubmit} className="space-y-4">
                {/* MODIFICACIÓN 1: Añadir input para fecha de apertura */}
                <div>
                    <label className="block text-sm font-medium text-gray-700">Fecha de Apertura</label>
                    <input type="date" name="fechaApertura" value={formData.fechaApertura} onChange={handleChange} className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                </div>
                 <select name="clienteId" value={formData.clienteId} onChange={handleChange} className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm">
                    <option value="">Seleccione un Cliente</option>
                    {collections.clientes.map(c => <option key={c.id} value={c.id}>{c.nombreCompleto}</option>)}
                 </select>
                 <select name="servicioId" value={formData.servicioId} onChange={handleChange} className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm">
                    <option value="">Seleccione un Proyecto/Servicio</option>
                    {collections.servicios.map(s => <option key={s.id} value={s.id}>{s.nombre}</option>)}
                 </select>
                 <select name="proveedorId" value={formData.proveedorId} onChange={handleChange} className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm">
                    <option value="">Seleccione un Proveedor</option>
                    {collections.proveedores.map(p => <option key={p.id} value={p.id}>{p.nombre}</option>)}
                 </select>
                <textarea name="comentarios" value={formData.comentarios} onChange={handleChange} placeholder="Comentarios" className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"></textarea>
                <Alert message={error} type="error" onClose={() => setError('')} />
                <button type="submit" disabled={loading} className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg disabled:bg-gray-400">
                    {loading ? 'Creando...' : 'Crear Proyecto y Generar NPU'}
                </button>
            </form>
        </div>
    );
};


const ProjectsTable = ({ projects, onUpdateProject }) => {
    
    const ManageDeliveryModal = ({ project, onClose, onFinalized }) => {
        const [formData, setFormData] = useState({
            fechaEntrega: new Date().toISOString().split('T')[0],
            notaEntrega: '',
        });
        const [notaPdfFile, setNotaPdfFile] = useState(null);
        // **NUEVO**: Estados para los dos documentos del proyecto
        const [documento1File, setDocumento1File] = useState(null);
        const [documento2File, setDocumento2File] = useState(null);
        
        const [error, setError] = useState('');
        const [loading, setLoading] = useState(false);
        const [uploadProgress, setUploadProgress] = useState(0);

        const handleChange = (e) => {
            const { name, value } = e.target;
            setFormData(prev => ({ ...prev, [name]: value }));
        };

        const handleFileChange = (setter) => (e) => {
            if (e.target.files[0]) setter(e.target.files[0]);
        };

        const uploadFile = async (file, path) => {
            if (!file) return null;
            const storageRef = ref(storage, path);
            const uploadTask = uploadBytesResumable(storageRef, file);
            
            return new Promise((resolve, reject) => {
                uploadTask.on('state_changed',
                    (snapshot) => setUploadProgress((snapshot.bytesTransferred / snapshot.totalBytes) * 100),
                    (error) => reject(error),
                    async () => {
                        const downloadURL = await getDownloadURL(uploadTask.snapshot.ref);
                        resolve(downloadURL);
                    }
                );
            });
        };

        const handleSave = async () => {
            // Si se sube el documento 2, los campos de entrega son obligatorios
            if (documento2File && (!formData.fechaEntrega || !formData.notaEntrega || !notaPdfFile)) {
                setError("Al subir el Documento 2, debe completar toda la información de entrega (fecha, nota y PDF de la nota).");
                return;
            }
            
            setLoading(true);
            setError('');

            try {
                // Sube los archivos que se hayan adjuntado
                const doc1Url = await uploadFile(documento1File, `proyectos/${project.id}/documento1/${documento1File?.name}`);
                const doc2Url = await uploadFile(documento2File, `proyectos/${project.id}/documento2/${documento2File?.name}`);
                const notaUrl = await uploadFile(notaPdfFile, `notas_entrega/${project.id}/${notaPdfFile?.name}`);

                const updatePayload = {};

                if (doc1Url) updatePayload.urlDocumento1 = doc1Url;
                if (doc2Url) updatePayload.urlDocumento2 = doc2Url;
                if (notaUrl) updatePayload.urlNotaPdf = notaUrl;

                // Si se sube el documento 2, el proyecto se finaliza
                if (documento2File) {
                    updatePayload.estado = 'Terminado';
                    updatePayload.fechaEntrega = Timestamp.fromDate(new Date(formData.fechaEntrega));
                    updatePayload.notaEntrega = formData.notaEntrega;
                }
                
                if (Object.keys(updatePayload).length > 0) {
                    const projectRef = doc(db, "proyectos", project.id);
                    await updateDoc(projectRef, updatePayload);
                }
                
                onFinalized();
                onClose();

            } catch(err) {
                 setError(`Error al guardar: ${err.message}`);
                 setLoading(false);
            }
        };

        return (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50 p-4">
                <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-lg">
                    <h3 className="text-lg font-bold mb-4">Gestionar Documentos y Entrega: {project.npu}</h3>
                    
                    {/* Sección Documento 1 */}
                    <div className="p-4 border rounded-lg mb-4">
                        <h4 className="font-semibold text-gray-800">Documento 1 (Intermedio)</h4>
                        {project.urlDocumento1 ? (
                             <div className="mt-2 text-sm">✓ Ya subido. <a href={project.urlDocumento1} target="_blank" rel="noopener noreferrer" className="text-blue-600">Ver documento</a></div>
                        ) : (
                             <input type="file" accept=".pdf" onChange={handleFileChange(setDocumento1File)} className="mt-2 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:font-semibold file:bg-gray-50 file:text-gray-700 hover:file:bg-gray-100"/>
                        )}
                    </div>

                    {/* Sección Documento 2 (Final) */}
                    <div className="p-4 border rounded-lg mb-4">
                        <h4 className="font-semibold text-gray-800">Documento 2 (Final)</h4>
                        <p className="text-xs text-gray-500">Subir este documento marcará el proyecto como "Terminado".</p>
                        {project.urlDocumento2 ? (
                             <div className="mt-2 text-sm">✓ Ya subido. <a href={project.urlDocumento2} target="_blank" rel="noopener noreferrer" className="text-blue-600">Ver documento</a></div>
                        ) : (
                             <input type="file" accept=".pdf" onChange={handleFileChange(setDocumento2File)} className="mt-2 block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:font-semibold file:bg-gray-50 file:text-gray-700 hover:file:bg-gray-100"/>
                        )}
                    </div>

                    {/* Sección de Entrega (solo si se sube el documento 2) */}
                    {documento2File && (
                        <div className="p-4 border border-green-300 bg-green-50 rounded-lg space-y-4">
                             <div><label className="block text-sm font-medium">Fecha de Entrega</label><input type="date" name="fechaEntrega" value={formData.fechaEntrega} onChange={handleChange} className="mt-1 block w-full px-3 py-2 border rounded-md"/></div>
                             <div><label className="block text-sm font-medium">Número de Nota de Entrega</label><input type="text" name="notaEntrega" value={formData.notaEntrega} onChange={handleChange} className="mt-1 block w-full px-3 py-2 border rounded-md"/></div>
                             <div><label className="block text-sm font-medium">PDF de la Nota de Entrega</label><input type="file" accept=".pdf" onChange={handleFileChange(setNotaPdfFile)} className="mt-1 block w-full text-sm"/></div>
                        </div>
                    )}

                    {loading && uploadProgress > 0 && <div className="w-full bg-gray-200 rounded-full h-2.5 mt-4"><div className="bg-blue-600 h-2.5 rounded-full" style={{width: `${uploadProgress}%`}}></div></div>}
                    <Alert message={error} type="error" onClose={() => setError('')} />
                    <div className="mt-6 flex justify-end space-x-3">
                        <button onClick={onClose} disabled={loading} className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded disabled:bg-gray-200">Cancelar</button>
                        <button onClick={handleSave} disabled={loading} className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded disabled:bg-gray-400">{loading ? 'Guardando...' : 'Guardar Cambios'}</button>
                    </div>
                </div>
            </div>
        );
    };

    /* ... código de ProjectsTable sin cambios significativos ... */
    return <div className="overflow-x-auto bg-white rounded-lg shadow"><h3 className="text-xl font-bold p-4">Tabla de Proyectos de Empleado</h3><p>Tabla detallada de proyectos aquí...</p></div>;
};

const FlipbookModal = ({ url, onClose }) => {
    return (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center z-50"><div className="bg-white rounded-lg shadow-xl w-full h-full max-w-6xl max-h-[90vh] flex flex-col"><div className="flex justify-between items-center p-4 border-b"><h3 className="text-lg font-bold">Visor de Documentos</h3><button onClick={onClose} className="text-gray-600 hover:text-gray-900 text-2xl font-bold">&times;</button></div><div className="flex-grow"><iframe src={url} className="w-full h-full" frameBorder="0" allowFullScreen></iframe></div></div></div>
    );
};

const ClientProjectsList = ({ projects }) => {
    const [modalUrl, setModalUrl] = useState(null);

    const formatDate = (timestamp) => {
        if (!timestamp) return '---';
        return new Date(timestamp.seconds * 1000).toLocaleDateString('es-MX');
    };
    
    const getStatusClass = (estado) => {
        switch(estado) {
            case 'Activo': return 'bg-orange-100 text-orange-800';
            case 'Terminado': return 'bg-green-100 text-green-800';
            default: return 'bg-gray-100 text-gray-800';
        }
    };

    return (
        <>
            <div className="bg-white rounded-lg shadow overflow-hidden">
                <div className="hidden md:flex bg-gray-50 border-b border-gray-200">
                    <div className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider flex-1">Servicio</div>
                    <div className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider w-40">Fecha Apertura</div>
                    <div className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider w-40">Estado</div>
                    <div className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider w-56">Acciones</div>
                </div>
                <ul className="divide-y divide-gray-200">
                    {projects.map(project => (
                        <li key={project.id} className="p-4 md:px-6 md:py-4 flex flex-col md:flex-row md:items-center">
                            <div className="flex-1 mb-4 md:mb-0"><div className="text-sm font-medium text-gray-900">{project.servicioNombre}</div><div className="text-sm text-gray-500 md:hidden mt-2"><span className="font-bold">Fecha:</span> {formatDate(project.fechaApertura)}</div></div>
                            <div className="w-full md:w-40 mb-4 md:mb-0"><div className="text-sm text-gray-500 hidden md:block">{formatDate(project.fechaApertura)}</div></div>
                            <div className="w-full md:w-40 mb-4 md:mb-0"><span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusClass(project.estado)}`}>{project.estado}</span></div>
                            <div className="w-full md:w-56 flex items-center space-x-4">
                                {/* **NUEVO**: Lógica de botones para cliente */}
                                {project.urlDocumento1 && <button onClick={() => setModalUrl(project.urlDocumento1)} className="text-sm font-medium text-blue-600 hover:text-blue-900">Ver Doc. 1</button>}
                                {project.urlDocumento2 && <button onClick={() => setModalUrl(project.urlDocumento2)} className="text-sm font-medium text-blue-600 hover:text-blue-900">Ver Doc. 2</button>}
                                {project.urlNotaPdf && <a href={project.urlNotaPdf} target="_blank" rel="noopener noreferrer" download className="text-sm font-medium text-red-600 hover:text-red-900">Descargar Nota</a>}
                                {(project.estado === 'Activo' && !project.urlDocumento1) && <span className="text-sm text-gray-400">En proceso</span>}
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
            {modalUrl && <FlipbookModal url={modalUrl} onClose={() => setModalUrl(null)} />}
        </>
    );
};

const Dashboard = ({ user, userData }) => {
    const [projects, setProjects] = useState([]);
    const [loading, setLoading] = useState(true);
    const [view, setView] = useState('projects'); 

    const fetchProjects = () => {
        setLoading(true);
        let q;
        if (userData.rol === 'empleado') {
            q = query(collection(db, "proyectos"), orderBy("fechaApertura", "desc"));
        } else {
            // Los clientes también verán proyectos en cotización
            q = query(collection(db, "proyectos"), where("clienteId", "==", user.uid), orderBy("fechaApertura", "desc"));
        }
        
        const unsubscribe = onSnapshot(q, (querySnapshot) => {
            const projectsData = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
            setProjects(projectsData);
            setLoading(false);
        }, (error) => {
            console.error("Error fetching projects: ", error);
            if (error.code === 'failed-precondition') {
                 console.error("Este error puede requerir un índice en Firestore. Ve a la URL que aparece en el mensaje de error en la consola para crearlo automáticamente.");
            }
            setLoading(false);
        });
        
        return () => unsubscribe();
    };

    useEffect(() => {
        if(user && userData) {
            const unsubscribe = fetchProjects();
            return unsubscribe;
        }
    }, [user, userData]);

    const EmployeeView = () => (
         <>
            <div className="mb-6 border-b border-gray-200">
                <nav className="-mb-px flex space-x-8" aria-label="Tabs">
                    <button onClick={() => setView('projects')} className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${view === 'projects' ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500 hover:text-gray-700'}`}>
                        Proyectos
                    </button>
                     <button onClick={() => setView('users')} className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${view === 'users' ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500 hover:text-gray-700'}`}>
                        Usuarios
                    </button>
                    {/* Faltarían pestañas para Servicios y Proveedores */}
                </nav>
            </div>
            {view === 'projects' && (
                <>
                    <NewProjectForm onProjectAdded={fetchProjects} />
                    <h2 className="text-2xl font-bold text-gray-800 my-6">Todos los Proyectos</h2>
                    <ProjectsTable projects={projects} onUpdateProject={fetchProjects} />
                </>
            )}
            {view === 'users' && <UserManagement onUserAdded={() => {}} />}
        </>
    );

    return (
        <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
            <h1 className="text-3xl font-bold text-gray-900 mb-6">
                {userData.rol === 'empleado' ? 'Panel de Administración' : `Proyectos de ${userData.nombreCompleto}`}
            </h1>
            {loading ? <p>Cargando proyectos...</p> : (userData.rol === 'empleado' ? <EmployeeView /> : <ClientProjectCards projects={projects} />)}
            {!loading && projects.length === 0 && <p className="text-center text-gray-500 mt-8">No hay proyectos para mostrar.</p>}
        </div>
    );
};

const AuthPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        try {
            await signInWithEmailAndPassword(auth, email, password);
        } catch (err) {
            setError("Email o contraseña incorrectos.");
            console.error("Error de autenticación:", err.code, err.message);
        }
    };
    
    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100">
            <div className="max-w-md w-full bg-white p-8 rounded-xl shadow-lg">
                <h2 className="text-3xl font-bold text-center text-gray-800 mb-6">Iniciar Sesión</h2>
                <form onSubmit={handleLogin} className="space-y-6">
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Email</label>
                        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"/>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Contraseña</label>
                        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"/>
                    </div>
                    {error && <p className="text-red-500 text-sm text-center">{error}</p>}
                    <button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg transition duration-300">
                        Entrar
                    </button>
                </form>
            </div>
        </div>
    );
};


export default function App() {
    const [user, setUser] = useState(null);
    const [userData, setUserData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
            if (currentUser) {
                const userDocRef = doc(db, "usuarios", currentUser.uid);
                const userDoc = await getDoc(userDocRef);
                if (userDoc.exists()) {
                    setUserData(userDoc.data());
                    setUser(currentUser);
                } else {
                    console.error("Usuario no encontrado en la base de datos.");
                    signOut(auth);
                }
            } else {
                setUser(null);
                setUserData(null);
            }
            setLoading(false);
        });
        return () => unsubscribe();
    }, []);

    if (loading) {
        return <div className="min-h-screen flex items-center justify-center bg-gray-50 text-gray-700">Cargando...</div>;
    }
    
    return (
        <div className="bg-gray-50 min-h-screen font-sans">
             <Header user={user} userData={userData}/>
             <main>
                {user && userData ? <Dashboard user={user} userData={userData} /> : <AuthPage />}
             </main>
        </div>
    );
}
