import React, { useState, useEffect } from 'react';
import { initializeApp } from 'firebase/app';
import { 
    getAuth, 
    onAuthStateChanged, 
    createUserWithEmailAndPassword, 
    signInWithEmailAndPassword, 
    signOut 
} from 'firebase/auth';
import { 
    getFirestore, 
    collection, 
    addDoc, 
    query, 
    where, 
    getDocs, 
    doc, 
    getDoc,
    updateDoc,
    onSnapshot,
    setDoc,
    Timestamp,
    runTransaction,
    orderBy,
    deleteDoc
} from 'firebase/firestore';
import { 
    getStorage, 
    ref, 
    uploadBytesResumable, 
    getDownloadURL 
} from 'firebase/storage';

// --- INSTRUCCIONES DE CONFIGURACIÓN DE FIREBASE ---
const firebaseConfig = {
  apiKey: "AIzaSyA7H6G0mXCy9DmoLg3fSW3TrxIRcEzz9jg",
  authDomain: "portal-evelsa.firebaseapp.com",
  projectId: "portal-evelsa",
  storageBucket: "portal-evelsa.firebasestorage.app",
  messagingSenderId: "847174487471",
  appId: "1:847174487471:web:c3a57fd8315ce619a2335a"
};

// --- Inicialización de Firebase ---
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const db = getFirestore(app);
const storage = getStorage(app);

// --- Componentes Reutilizables ---
const Alert = ({ message, type, onClose }) => {
    if (!message) return null;
    const baseClasses = "p-4 mb-4 text-sm rounded-lg relative";
    const typeClasses = { error: "bg-red-100 text-red-700", success: "bg-green-100 text-green-700" };
    return (
        <div className={`${baseClasses} ${typeClasses[type]}`} role="alert">
            <span className="font-medium">{message}</span>
            <button onClick={onClose} className="absolute top-0 right-0 mt-2 mr-3 text-2xl font-semibold leading-none">&times;</button>
        </div>
    );
};

// --- Componente de Cabecera ---
const Header = ({ user, userData }) => {
    // URL de tu logo
    const logoGrupoEvelsa = "hhttps://www.grupoevelsa.com/assets/images/evelsa.png";

    return (
        <header className="bg-white shadow-md sticky top-0 z-40">
            <div className="max-w-7xl mx-auto py-3 px-4 sm:px-6 lg:px-8 flex justify-between items-center">
                <div className="flex items-center">
                    {/* Lógica para mostrar logo de cliente o el tuyo */}
                    {userData && userData.rol === 'cliente' && userData.logoUrl ? (
                        <img src={userData.logoUrl} onError={(e)=>{e.target.onerror = null; e.target.src="https://placehold.co/150x50/FFFFFF/000000?text=Logo+Cliente"}} alt="Logo Cliente" className="h-12 w-auto mr-4 object-contain"/>
                    ) : (
                        <img src={logoGrupoEvelsa} alt="Logo Grupo Evelsa" className="h-12 w-auto mr-4"/>
                    )}
                </div>
                {user && (
                    <div className="flex items-center">
                        <span className="text-gray-600 mr-4 hidden sm:block">
                            {/* Saludo más corto */}
                            Hola, {userData ? userData.nombreCompleto.split(' ')[0] : user.email}
                        </span>
                        <button
                            onClick={() => signOut(auth)}
                            className="bg-red-600 hover:bg-red-700 text-white font-bold py-2 px-4 rounded-lg transition duration-300"
                        >
                            Salir
                        </button>
                    </div>
                )}
            </div>
        </header>
    );
};

const UserManagement = ({ onUserAdded }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [nombreCompleto, setNombreCompleto] = useState('');
    const [rol, setRol] = useState('cliente');
    const [clienteIdNumerico, setClienteIdNumerico] = useState('');
    const [logoUrl, setLogoUrl] = useState('');
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');

    const resetForm = () => {
        setEmail(''); setPassword(''); setNombreCompleto(''); setRol('cliente');
        setClienteIdNumerico(''); setLogoUrl('');
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(''); setSuccess('');

        if (!nombreCompleto || !email || !password || (rol === 'cliente' && !clienteIdNumerico)) {
            setError('Por favor, complete todos los campos requeridos.');
            return;
        }

        try {
            // Se usa una instancia secundaria para no desloguear al admin
            const tempApp = initializeApp(firebaseConfig, `Secondary-${new Date().getTime()}`);
            const tempAuth = getAuth(tempApp);
            const userCredential = await createUserWithEmailAndPassword(tempAuth, email, password);
            
            await setDoc(doc(db, "usuarios", userCredential.user.uid), {
                email, rol, nombreCompleto,
                clienteIdNumerico: rol === 'cliente' ? clienteIdNumerico.padStart(3, '0') : '',
                logoUrl: rol === 'cliente' ? logoUrl : '',
            });

            setSuccess(`¡Usuario ${email} creado con éxito!`);
            resetForm();
            if (onUserAdded) onUserAdded();
        } catch (err) {
            setError(`Error al crear usuario: ${err.message}`);
        }
    };
    
    return (
        <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200">
            <h3 className="text-xl font-bold text-gray-800 mb-4">Crear Usuario (Cliente o Empleado)</h3>
            <form onSubmit={handleSubmit} className="space-y-4">
                 <input type="text" value={nombreCompleto} onChange={e => setNombreCompleto(e.target.value)} placeholder="Nombre Completo" className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                 <select value={rol} onChange={e => setRol(e.target.value)} className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm">
                    <option value="cliente">Cliente</option>
                    <option value="empleado">Empleado</option>
                 </select>
                 <input type="email" value={email} onChange={e => setEmail(e.target.value)} placeholder="Email de Acceso" className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                 <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Contraseña (mínimo 6 caracteres)" className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                 {rol === 'cliente' && (
                     <>
                        <input type="text" value={clienteIdNumerico} onChange={e => setClienteIdNumerico(e.target.value)} placeholder="ID Numérico Cliente (ej: 001)" className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                        <input type="text" value={logoUrl} onChange={e => setLogoUrl(e.target.value)} placeholder="URL del Logo (Opcional)" className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                     </>
                 )}
                 <Alert message={error} type="error" onClose={() => setError('')} />
                 <Alert message={success} type="success" onClose={() => setSuccess('')} />
                 <button type="submit" className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg">Crear Usuario</button>
            </form>
        </div>
    );
};

// --- Componentes de Empleado ---
const NewProjectForm = ({ onProjectAdded }) => {
    const [formData, setFormData] = useState({ 
        clienteId: '', 
        servicioId: '', 
        proveedorId: '', 
        comentarios: '',
        fechaApertura: new Date().toISOString().split('T')[0]
    });
    const [collections, setCollections] = useState({ clientes: [], servicios: [], proveedores: [] });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [clientesSnap, serviciosSnap, proveedoresSnap] = await Promise.all([
                    getDocs(query(collection(db, "usuarios"), where("rol", "==", "cliente"))),
                    getDocs(collection(db, "servicios")),
                    getDocs(collection(db, "proveedores"))
                ]);
                setCollections({
                    clientes: clientesSnap.docs.map(doc => ({ id: doc.id, ...doc.data() })),
                    servicios: serviciosSnap.docs.map(doc => ({ id: doc.id, ...doc.data() })),
                    proveedores: proveedoresSnap.docs.map(doc => ({ id: doc.id, ...doc.data() }))
                });
            } catch (e) {
                setError("Error al cargar datos. Asegúrese de que las colecciones 'servicios' y 'proveedores' existan.");
            }
        };
        fetchData();
    }, []);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!formData.clienteId || !formData.servicioId || !formData.proveedorId || !formData.fechaApertura) {
            setError('Todos los campos son obligatorios.');
            return;
        }
        setLoading(true);
        setError('');

        try {
            const anioActual = new Date(formData.fechaApertura).getFullYear();
            const contadorRef = doc(db, "contadores", `proyectos_${anioActual}`);
            
            const nuevoConsecutivo = await runTransaction(db, async (transaction) => {
                const contadorDoc = await transaction.get(contadorRef);
                if (!contadorDoc.exists()) {
                    transaction.set(contadorRef, { consecutivo: 1 });
                    return 1;
                }
                const nuevoValor = contadorDoc.data().consecutivo + 1;
                transaction.update(contadorRef, { consecutivo: nuevoValor });
                return nuevoValor;
            });
            
            const ultimosDosDigitosAnio = anioActual.toString().slice(-2);
            const consecutivoFormateado = nuevoConsecutivo.toString().padStart(3, '0');
            
            const cliente = collections.clientes.find(c => c.id === formData.clienteId);
            const servicio = collections.servicios.find(s => s.id === formData.servicioId);
            const proveedor = collections.proveedores.find(p => p.id === formData.proveedorId);

            const npu = `${cliente.clienteIdNumerico}-${servicio.servicioIdNumerico}-${proveedor.proveedorIdNumerico}-${consecutivoFormateado}${ultimosDosDigitosAnio}`;

            await addDoc(collection(db, "proyectos"), {
                fechaApertura: Timestamp.fromDate(new Date(formData.fechaApertura)),
                npu,
                clienteId: cliente.id,
                clienteNombre: cliente.nombreCompleto,
                servicioId: servicio.id,
                servicioNombre: servicio.nombre,
                proveedorId: proveedor.id,
                proveedorNombre: proveedor.nombre,
                comentarios: formData.comentarios,
                estado: 'Cotización',
                fechaEntrega1: null,
                notaEntrega1: '',
                urlNotaPdf1: '',
                urlDocumento1: '',
                fechaEntrega2: null,
                notaEntrega2: '',
                urlNotaPdf2: '',
                urlDocumento2: ''
            });
            
            setFormData({ clienteId: '', servicioId: '', proveedorId: '', comentarios: '', fechaApertura: new Date().toISOString().split('T')[0] });
            if (onProjectAdded) onProjectAdded();

        } catch (err) {
            console.error("Error al crear proyecto:", err);
            setError(`Ocurrió un error: ${err.message}`);
        }
        setLoading(false);
    };

    return (
        <div className="bg-white p-6 rounded-xl shadow-lg border border-gray-200 mb-8">
            <h3 className="text-xl font-bold text-gray-800 mb-4">Crear Nuevo Proyecto</h3>
            <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                    <label className="block text-sm font-medium text-gray-700">Fecha de Apertura</label>
                    <input type="date" name="fechaApertura" value={formData.fechaApertura} onChange={handleChange} className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                </div>
                 <select name="clienteId" value={formData.clienteId} onChange={handleChange} className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm">
                    <option value="">Seleccione un Cliente</option>
                    {collections.clientes.map(c => <option key={c.id} value={c.id}>{c.nombreCompleto}</option>)}
                 </select>
                 <select name="servicioId" value={formData.servicioId} onChange={handleChange} className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm">
                    <option value="">Seleccione un Proyecto/Servicio</option>
                    {collections.servicios.map(s => <option key={s.id} value={s.id}>{s.nombre}</option>)}
                 </select>
                 <select name="proveedorId" value={formData.proveedorId} onChange={handleChange} className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm">
                    <option value="">Seleccione un Proveedor</option>
                    {collections.proveedores.map(p => <option key={p.id} value={p.id}>{p.nombre}</option>)}
                 </select>
                <textarea name="comentarios" value={formData.comentarios} onChange={handleChange} placeholder="Comentarios" className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"></textarea>
                <Alert message={error} type="error" onClose={() => setError('')} />
                <button type="submit" disabled={loading} className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-lg disabled:bg-gray-400">
                    {loading ? 'Creando...' : 'Crear Proyecto y Generar NPU'}
                </button>
            </form>
        </div>
    );
};


const ProjectsTable = ({ projects, onUpdateProject }) => {
    
    const ManageDeliveryModal = ({ project, onClose, onFinalized }) => {
        const [formData1, setFormData1] = useState({
            fechaEntrega: project.fechaEntrega1 ? project.fechaEntrega1.toDate().toISOString().split('T')[0] : '',
            notaEntrega: project.notaEntrega1 || '',
            urlDocumento: project.urlDocumento1 || ''
        });
        const [notaPdfFile1, setNotaPdfFile1] = useState(null);

        const [formData2, setFormData2] = useState({
            fechaEntrega: project.fechaEntrega2 ? project.fechaEntrega2.toDate().toISOString().split('T')[0] : '',
            notaEntrega: project.notaEntrega2 || '',
            urlDocumento: project.urlDocumento2 || ''
        });
        const [notaPdfFile2, setNotaPdfFile2] = useState(null);
        
        const [error, setError] = useState('');
        const [loading, setLoading] = useState(false);

        const handleChange = (setter) => (e) => {
            const { name, value } = e.target;
            setter(prev => ({ ...prev, [name]: value }));
        };

        const handleFileChange = (setter) => (e) => {
            if (e.target.files[0]) setter(e.target.files[0]);
        };

        const uploadFile = async (file, path) => {
            if (!file) return null;
            const storageRef = ref(storage, path);
            const uploadTask = uploadBytesResumable(storageRef, file);
            return await getDownloadURL(uploadTask.snapshot.ref);
        };

        const handleSave = async () => {
            setLoading(true);
            setError('');
            try {
                const updatePayload = {};

                const nota1Url = await uploadFile(notaPdfFile1, `notas_entrega/${project.id}/nota1_${notaPdfFile1?.name}`);
                if (nota1Url) updatePayload.urlNotaPdf1 = nota1Url;
                if (formData1.urlDocumento) updatePayload.urlDocumento1 = formData1.urlDocumento;
                if (formData1.notaEntrega) updatePayload.notaEntrega1 = formData1.notaEntrega;
                if (formData1.fechaEntrega) updatePayload.fechaEntrega1 = Timestamp.fromDate(new Date(formData1.fechaEntrega));

                const nota2Url = await uploadFile(notaPdfFile2, `notas_entrega/${project.id}/nota2_${notaPdfFile2?.name}`);
                if (nota2Url) updatePayload.urlNotaPdf2 = nota2Url;
                if(formData2.urlDocumento) updatePayload.urlDocumento2 = formData2.urlDocumento;
                if (formData2.notaEntrega) updatePayload.notaEntrega2 = formData2.notaEntrega;
                if (formData2.fechaEntrega) updatePayload.fechaEntrega2 = Timestamp.fromDate(new Date(formData2.fechaEntrega));

                if (formData2.urlDocumento) {
                    if (!formData2.fechaEntrega || !formData2.notaEntrega || (!notaPdfFile2 && !project.urlNotaPdf2)) {
                        setError("Al finalizar con el Documento 2, debe completar toda la información de entrega.");
                        setLoading(false);
                        return;
                    }
                    updatePayload.estado = 'Terminado';
                }
                
                if (Object.keys(updatePayload).length > 0) {
                    const projectRef = doc(db, "proyectos", project.id);
                    await updateDoc(projectRef, updatePayload);
                }
                
                onFinalized();
                onClose();

            } catch(err) {
                 setError(`Error al guardar: ${err.message}`);
                 setLoading(false);
            }
        };

        return (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50 p-4">
                <div className="bg-white p-6 rounded-lg shadow-xl w-full max-w-lg">
                    <h3 className="text-lg font-bold mb-4">Gestionar Documentos y Entrega: {project.npu}</h3>
                    
                    <div className="p-4 border rounded-lg mb-4 space-y-3">
                        <h4 className="font-semibold text-gray-800">Documento 1 (Intermedio)</h4>
                        <input type="text" name="urlDocumento" placeholder="Pegar enlace de Heyzine para Doc. 1" value={formData1.urlDocumento} onChange={handleChange(setFormData1)} className="block w-full px-2 py-1 border rounded-md"/>
                        <hr/>
                        <h5 className="text-sm font-semibold">Nota de Entrega del Documento 1</h5>
                        <input type="date" name="fechaEntrega" value={formData1.fechaEntrega} onChange={handleChange(setFormData1)} className="block w-full px-2 py-1 border rounded-md"/>
                        <input type="text" name="notaEntrega" placeholder="Número de nota de entrega 1" value={formData1.notaEntrega} onChange={handleChange(setFormData1)} className="block w-full px-2 py-1 border rounded-md"/>
                        <input type="file" accept=".pdf" onChange={handleFileChange(setNotaPdfFile1)} className="block w-full text-sm"/>
                    </div>

                    <div className="p-4 border rounded-lg mb-4 space-y-3">
                        <h4 className="font-semibold text-gray-800">Documento 2 (Final)</h4>
                        <p className="text-xs text-gray-500">Añadir un enlace aquí marcará el proyecto como "Terminado".</p>
                        <input type="text" name="urlDocumento" placeholder="Pegar enlace de Heyzine para Doc. 2" value={formData2.urlDocumento} onChange={handleChange(setFormData2)} className="block w-full px-2 py-1 border rounded-md"/>
                        <hr/>
                        <h5 className="text-sm font-semibold">Nota de Entrega del Documento 2</h5>
                        <input type="date" name="fechaEntrega" value={formData2.fechaEntrega} onChange={handleChange(setFormData2)} className="block w-full px-2 py-1 border rounded-md"/>
                        <input type="text" name="notaEntrega" placeholder="Número de nota de entrega 2" value={formData2.notaEntrega} onChange={handleChange(setFormData2)} className="block w-full px-2 py-1 border rounded-md"/>
                        <input type="file" accept=".pdf" onChange={handleFileChange(setNotaPdfFile2)} className="block w-full text-sm"/>
                    </div>

                    <Alert message={error} type="error" onClose={() => setError('')} />
                    <div className="mt-6 flex justify-end space-x-3">
                        <button onClick={onClose} disabled={loading} className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded">Cancelar</button>
                        <button onClick={handleSave} disabled={loading} className="bg-[#b0ef26] hover:bg-[#9ac91e] text-black font-bold py-2 px-4 rounded">{loading ? 'Guardando...' : 'Guardar Cambios'}</button>
                    </div>
                </div>
            </div>
        );
    };

    const [modalProject, setModalProject] = useState(null);

    const formatDate = (timestamp) => {
        if (!timestamp) return '---';
        return new Date(timestamp.seconds * 1000).toLocaleDateString('es-MX');
    };

    const handleActivateProject = async (projectId) => {
        await updateDoc(doc(db, "proyectos", projectId), { estado: 'Activo' });
        onUpdateProject();
    };

    const handleDeleteProject = async (projectId, projectNpu) => {
        if (window.confirm(`¿Estás seguro de que quieres borrar el proyecto ${projectNpu}?`)) {
            await deleteDoc(doc(db, "proyectos", projectId));
        }
    };

    return (
        <>
            <div className="overflow-x-auto bg-white rounded-lg shadow">
                <table className="min-w-full divide-y divide-gray-200">
                     <thead className="bg-gray-50">
                        <tr>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">NPU</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Cliente</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Servicio</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Fecha Apertura</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Estado</th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Acciones</th>
                        </tr>
                    </thead>
                    <tbody className="bg-white divide-y divide-gray-200">
                        {projects.map(project => (
                             <tr key={project.id}>
                                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">{project.npu}</td>
                                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{project.clienteNombre}</td>
                                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{project.servicioNombre}</td>
                                <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">{formatDate(project.fechaApertura)}</td>
                                <td className="px-6 py-4 whitespace-nowrap">
                                    <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${
                                        project.estado === 'Activo' ? 'bg-orange-100 text-orange-800' :
                                        project.estado === 'Terminado' ? 'bg-green-100 text-green-800' :
                                        'bg-gray-100 text-gray-800'
                                    }`}>{project.estado}</span>
                                </td>
                                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                                    <div className="flex items-center space-x-4">
                                        {project.estado === 'Cotización' && <button onClick={() => handleActivateProject(project.id)} className="text-green-600 hover:text-green-900">Activar</button>}
                                        {project.estado === 'Activo' && <button onClick={() => setModalProject(project)} className="text-indigo-600 hover:text-indigo-900">Gestionar</button>}
                                        {project.estado === 'Terminado' && <span className="text-gray-400">Finalizado</span>}
                                        {project.estado !== 'Terminado' && <button onClick={() => handleDeleteProject(project.id, project.npu)} className="text-red-600 hover:text-red-900">Borrar</button>}
                                    </div>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            {modalProject && <ManageDeliveryModal project={modalProject} onClose={() => setModalProject(null)} onFinalized={onUpdateProject} />}
        </>
    );
};

// **NUEVO**: Componente Modal para el visor de Heyzine
const HeyzineViewerModal = ({ url, onClose }) => {
    return (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center z-50">
            <div className="bg-white rounded-lg shadow-xl w-full h-full max-w-6xl max-h-[90vh] flex flex-col">
                <div className="flex justify-between items-center p-4 border-b">
                    <h3 className="text-lg font-bold">Visor de Documentos</h3>
                    <button onClick={onClose} className="text-gray-600 hover:text-gray-900 text-2xl font-bold">&times;</button>
                </div>
                <div className="flex-grow">
                    <iframe src={url} className="w-full h-full" frameBorder="0" allowFullScreen></iframe>
                </div>
            </div>
        </div>
    );
};


// **CÓDIGO CORREGIDO PARA ClientProjectsList**
const ClientProjectsList = ({ projects }) => {
    // Estado para controlar la URL del modal
    const [modalUrl, setModalUrl] = useState(null);

    const formatDate = (timestamp) => {
        if (!timestamp) return '---';
        return new Date(timestamp.seconds * 1000).toLocaleDateString('es-MX');
    };
    
    const getStatusClass = (estado) => {
        switch(estado) {
            case 'Activo': return 'bg-orange-100 text-orange-800';
            case 'Terminado': return 'bg-green-100 text-green-800';
            default: return 'bg-gray-100 text-gray-800';
        }
    };

    return (
        <>
            <div className="bg-white rounded-lg shadow overflow-hidden">
                <div className="hidden md:flex bg-gray-50 border-b border-gray-200">
                    <div className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider flex-1">Servicio</div>
                    <div className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider w-40">Fecha Apertura</div>
                    <div className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider w-40">Estado</div>
                    <div className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider w-56">Acciones</div>
                </div>
                <ul className="divide-y divide-gray-200">
                    {projects.map(project => (
                        <li key={project.id} className="p-4 md:px-6 md:py-4 flex flex-col md:flex-row md:items-center">
                            <div className="flex-1 mb-4 md:mb-0">
                                <div className="text-sm font-medium text-gray-900">{project.servicioNombre}</div>
                                <div className="text-sm text-gray-500 md:hidden mt-2"><span className="font-bold">Fecha:</span> {formatDate(project.fechaApertura)}</div>
                            </div>
                            <div className="w-full md:w-40 mb-4 md:mb-0">
                               <div className="text-sm text-gray-500 hidden md:block">{formatDate(project.fechaApertura)}</div>
                            </div>
                            <div className="w-full md:w-40 mb-4 md:mb-0">
                                <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full ${getStatusClass(project.estado)}`}>{project.estado}</span>
                            </div>
                            <div className="w-full md:w-56 flex items-center space-x-4">
                                {/* CORRECCIÓN: Ahora son botones que abren el modal */}
                                {project.urlDocumento1 && <button onClick={() => setModalUrl(project.urlDocumento1)} className="text-sm font-medium text-blue-600 hover:text-blue-900">Ver Doc. 1</button>}
                                {project.urlDocumento2 && <button onClick={() => setModalUrl(project.urlDocumento2)} className="text-sm font-medium text-blue-600 hover:text-blue-900">Ver Doc. 2</button>}
                                {project.urlNotaPdf1 && <a href={project.urlNotaPdf1} target="_blank" rel="noopener noreferrer" download className="text-sm font-medium text-red-600 hover:text-red-900">Nota 1</a>}
                                {project.urlNotaPdf2 && <a href={project.urlNotaPdf2} target="_blank" rel="noopener noreferrer" download className="text-sm font-medium text-red-600 hover:text-red-900">Nota 2</a>}
                                {(project.estado === 'Activo' && !project.urlDocumento1) && <span className="text-sm text-gray-400">En proceso</span>}
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
            {/* Renderizado condicional del modal */}
            {modalUrl && <HeyzineViewerModal url={modalUrl} onClose={() => setModalUrl(null)} />}
        </>
    );
};


// --- Componente Principal y de Autenticación (COMPLETO) ---
const Dashboard = ({ user, userData }) => {
    const [projects, setProjects] = useState([]);
    const [loading, setLoading] = useState(true);
    const [view, setView] = useState('projects'); 

    const fetchProjects = () => {
        let q;
        if (userData.rol === 'empleado') {
            q = query(collection(db, "proyectos"), orderBy("fechaApertura", "desc"));
        } else {
            q = query(collection(db, "proyectos"), where("clienteId", "==", user.uid), where("estado", "in", ["Activo", "Terminado"]), orderBy("fechaApertura", "desc"));
        }
        
        return onSnapshot(q, (querySnapshot) => {
            const projectsData = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
            setProjects(projectsData);
            setLoading(false);
        }, (error) => {
            console.error("Error fetching projects: ", error);
            if (error.code === 'failed-precondition') console.error("IMPORTANTE: Se requiere un índice compuesto en Firestore.");
            setLoading(false);
        });
    };

    useEffect(() => {
        if(user && userData) {
            setLoading(true);
            const unsubscribe = fetchProjects();
            return () => unsubscribe();
        }
    }, [user, userData]);

    const EmployeeView = () => (
         <>
            <div className="mb-6 border-b border-gray-200"><nav className="-mb-px flex space-x-8"><button onClick={() => setView('projects')} className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${view === 'projects' ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500'}`}>Proyectos</button><button onClick={() => setView('users')} className={`whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm ${view === 'users' ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500'}`}>Usuarios</button></nav></div>
            {view === 'projects' && ( <> <NewProjectForm onProjectAdded={fetchProjects} /> <h2 className="text-2xl font-bold text-gray-800 my-6">Todos los Proyectos</h2> <ProjectsTable projects={projects} onUpdateProject={fetchProjects} /> </> )}
            {view === 'users' && <UserManagement onUserAdded={() => {}} />}
         </>
    );

    return (
        <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
            <h1 className="text-3xl font-bold text-gray-900 mb-6">{userData.rol === 'empleado' ? 'Panel de Administración' : `Proyectos de ${userData.nombreCompleto}`}</h1>
            {loading ? <p>Cargando proyectos...</p> : (
                userData.rol === 'empleado' ? <EmployeeView /> : <ClientProjectsList projects={projects} />
            )}
            {!loading && projects.length === 0 && <p className="text-center text-gray-500 mt-8">No hay proyectos para mostrar.</p>}
        </div>
    );
};

const AuthPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');
        try { 
            await signInWithEmailAndPassword(auth, email, password); 
        } catch (err) { 
            setError("Email o contraseña incorrectos."); 
        }
    };
    
    return (
        // Fondo con tu color gris
        <div className="min-h-screen flex items-center justify-center bg-[#cdcdcd]">
            <div className="max-w-md w-full bg-white p-8 rounded-xl shadow-2xl">
                {/* Logo añadido */}
                <img src="https://www.grupoevelsa.com/images/logo.png" alt="Logo Grupo Evelsa" className="h-16 mx-auto mb-6"/>
                <h2 className="text-2xl font-bold text-center text-gray-800 mb-8">Acceso al Portal</h2>
                <form onSubmit={handleLogin} className="space-y-6">
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Email</label>
                        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                    </div>
                    <div>
                        <label className="block text-sm font-medium text-gray-700">Contraseña</label>
                        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required className="mt-1 block w-full px-3 py-2 bg-white border border-gray-300 rounded-md shadow-sm"/>
                    </div>
                    {error && <p className="text-red-500 text-sm text-center">{error}</p>}
                    {/* Botón con tu color verde */}
                    <button type="submit" className="w-full bg-[#b0ef26] hover:bg-[#9ac91e] text-black font-bold py-3 px-4 rounded-lg transition duration-300 text-lg">
                        Entrar
                    </button>
                </form>
            </div>
        </div>
    );
};


export default function App() {
    const [user, setUser] = useState(null);
    const [userData, setUserData] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
            if (currentUser) {
                const userDocRef = doc(db, "usuarios", currentUser.uid);
                const userDoc = await getDoc(userDocRef);
                if (userDoc.exists()) {
                    setUserData(userDoc.data());
                    setUser(currentUser);
                } else { signOut(auth); }
            } else {
                setUser(null);
                setUserData(null);
            }
            setLoading(false);
        });
        return () => unsubscribe();
    }, []);

    if (loading) {
        return <div className="min-h-screen flex items-center justify-center bg-gray-50 text-gray-700">Cargando...</div>;
    }
    
    return (
        <div className="bg-[#c9c9c9] min-h-screen font-sans">
             <Header user={user} userData={userData}/>
             <main>
                {user && userData ? <Dashboard user={user} userData={userData} /> : <AuthPage />}
             </main>
        </div>
    );
}
